
function add(){
    let a=parseInt(document.getElementById('a').value);
    let b=parseInt(document.getElementById('b').value);
    let  c=a+b;
    document.getElementById("sol").innerHTML="Solution after addition is:"+c;

}
function sub(){
    let a=parseInt(document.getElementById('a').value);
    let b=parseInt(document.getElementById('b').value);
    let  c=a-b;
    document.getElementById("sol2").innerHTML="Solution after addition is:"+c;

}
